import React, { useEffect, useState } from "react";
import PlaceListComponent from "./PlaceListComponent"; // Ìó¨Ïä§Ïû• Î™©Î°ù Ïª¥Ìè¨ÎÑåÌä∏

const MapComponent = () => {
    const [userLat, setUserLat] = useState(null);
    const [userLng, setUserLng] = useState(null);

    useEffect(() => {
        let map = null;

        const initMap = (lat, lng) => {
            if (!window.naver || !window.naver.maps) {
                console.error("‚ùå ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ APIÍ∞Ä ÏïÑÏßÅ Î°úÎìúÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§.");
                return;
            }

            map = new window.naver.maps.Map('map', {
                center: new window.naver.maps.LatLng(lat, lng),
                zoom: 15,
            });

            new window.naver.maps.Marker({
                position: new window.naver.maps.LatLng(lat, lng),
                map,
                title: "ÎÇ¥ ÏúÑÏπò"
            });

            // üìå ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏ (PlaceListComponentÏóê ÎÑòÍ≤®Ï§Ñ Í∞í Ï†ÄÏû•)
            setUserLat(lat);
            setUserLng(lng);
        };

        const getCurrentPosition = () => {
            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(
                    (position) => {
                        const lat = position.coords.latitude;
                        const lng = position.coords.longitude;
                        initMap(lat, lng);
                    },
                    (error) => {
                        console.error("‚ùå Geolocation Error: ", error);
                        initMap(37.5665, 126.9780);
                    }, {
                        enableHighAccuracy: true,
                        timeout: 10000,
                        maximumAge: 0
                    }
                );
            } else {
                console.error("Geolocation is not supported by this browser.");
                initMap(37.5665, 126.9780);
            }
        };

        // ‚úÖ ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ Ïä§ÌÅ¨Î¶ΩÌä∏ Î°úÎìú
        const script = document.createElement('script');
        script.src = `https://openapi.map.naver.com/openapi/v3/maps.js?ncpClientId=${process.env.REACT_APP_NAVER_MAP_CLIENT_ID}`;
        script.async = true;
        script.onload = () => {
            if (!window.naver || !window.naver.maps) {
                console.error("‚ùå ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ API Î°úÎìú Ïã§Ìå®");
                return;
            }
            getCurrentPosition(); // ‚úÖ ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ API Î°úÎìú ÏôÑÎ£å ÌõÑ Ïã§Ìñâ
        };

        document.head.appendChild(script);
    }, []);

    return (
        <div>
            <div id="map" style={{ width: '100%', height: '400px' }} />

            {/* üåç userLat & userLngÍ∞Ä ÏÑ§Ï†ïÎêòÎ©¥ PlaceListComponent Î†åÎçîÎßÅ */}
            {userLat && userLng && <PlaceListComponent lat={userLat} lng={userLng} />}
        </div>
    );
};

export default MapComponent;
