import React, { useEffect, useState } from "react";
import axios from "axios";
import PlaceListComponent from "./PlaceListComponent"; // üìå Ìó¨Ïä§Ïû• Î™©Î°ù Ïª¥Ìè¨ÎÑåÌä∏ Ï∂îÍ∞Ä

const MapComponent = () => {
    const [map, setMap] = useState(null);
    const [userLat, setUserLat] = useState(null);
    const [userLng, setUserLng] = useState(null);
    const [gyms, setGyms] = useState([]); // üìå Ìó¨Ïä§Ïû• Î™©Î°ù Ï†ÄÏû•

    useEffect(() => {
        const initMap = (lat, lng) => {
            if (!window.naver || !window.naver.maps) {
                console.error("‚ùå ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ APIÍ∞Ä ÏïÑÏßÅ Î°úÎìúÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§.");
                return;
            }

            console.log("üìç ÏßÄÎèÑ Ï¥àÍ∏∞Ìôî: ", lat, lng);

            const mapInstance = new window.naver.maps.Map('map', {
                center: new window.naver.maps.LatLng(lat, lng),
                zoom: 15,
            });

            // üìå ÎÇ¥ ÏúÑÏπò ÎßàÏª§ Ï∂îÍ∞Ä
            new window.naver.maps.Marker({
                position: new window.naver.maps.LatLng(lat, lng),
                map: mapInstance,
                title: "ÎÇ¥ ÏúÑÏπò",
                icon: {
                    content: '<div style="color:red; font-weight:bold;">üìç</div>',
                    size: new window.naver.maps.Size(20, 20),
                    anchor: new window.naver.maps.Point(10, 10),
                }
            });

            setMap(mapInstance);
            setUserLat(lat);
            setUserLng(lng);
        };

        const getCurrentPosition = () => {
            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(
                    (position) => {
                        const lat = position.coords.latitude;
                        const lng = position.coords.longitude;
                        console.log("üìç ÌòÑÏû¨ ÏúÑÏπò: ", lat, lng);

                        initMap(lat, lng);
                    },
                    (error) => {
                        console.error("‚ùå Geolocation Error:", error);
                        initMap(37.5665, 126.9780);
                    }, {
                        enableHighAccuracy: true,
                        timeout: 10000,
                        maximumAge: 0
                    }
                );
            } else {
                console.error("‚ùå GeolocationÏùÑ ÏßÄÏõêÌïòÏßÄ ÏïäÎäî Î∏åÎùºÏö∞Ï†ÄÏûÖÎãàÎã§.");
                initMap(37.5665, 126.9780);
            }
        };

        // ‚úÖ ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ Ïä§ÌÅ¨Î¶ΩÌä∏ Î°úÎìú
        const script = document.createElement('script');
        script.src = `https://openapi.map.naver.com/openapi/v3/maps.js?ncpClientId=${process.env.REACT_APP_NAVER_MAP_CLIENT_ID}`;
        script.async = true;
        script.onload = getCurrentPosition;
        document.head.appendChild(script);
    }, []);

    // ‚úÖ Ìó¨Ïä§Ïû• Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞ (userLat, userLngÍ∞Ä ÏÑ§Ï†ïÎêòÎ©¥ Ïã§Ìñâ)
    useEffect(() => {
        if (userLat && userLng) {
            console.log("üîç Ìó¨Ïä§Ïû• Í≤ÄÏÉâ ÏãúÏûë:", userLat, userLng);
            fetchGyms(userLat, userLng);
        }
    }, [userLat, userLng]);

    const fetchGyms = async (lat, lng) => {
        try {
            console.log("üõ†Ô∏è API ÏöîÏ≤≠:", lat, lng);
            const response = await axios.get("http://localhost:8080/api/v1/places", {
                params: { lat, lng }
            });

            console.log("‚úÖ Í≤ÄÏÉâÎêú Ìó¨Ïä§Ïû• Î™©Î°ù:", response.data.items);
            setGyms(response.data.items);
        } catch (error) {
            console.error("‚ùå API Ìò∏Ï∂ú Ïò§Î•ò:", error);
        }
    };

    // ‚úÖ ÏßÄÎèÑ ÏúÑÏóê ÎßàÏª§ Ï∂îÍ∞Ä
    useEffect(() => {
        if (!map || gyms.length === 0) return;

        console.log("üìå ÏßÄÎèÑÏóê ÎßàÏª§ Ï∂îÍ∞Ä:", gyms);
        gyms.forEach((gym) => {
            new window.naver.maps.Marker({
                position: new window.naver.maps.LatLng(gym.mapy, gym.mapx), // üìå ÏúÑÎèÑ/Í≤ΩÎèÑ ÏàúÏÑú Î∞òÎåÄÎ°ú ÏÑ§Ï†ï
                map: map,
                title: gym.title,
                icon: {
                    content: `<div style="color:blue; font-weight:bold;">üèãÔ∏è‚Äç‚ôÇÔ∏è</div>`,
                    size: new window.naver.maps.Size(20, 20),
                    anchor: new window.naver.maps.Point(10, 10),
                }
            });
        });
    }, [map, gyms]);

    return (
        <div>
            <div id="map" style={{ width: '100%', height: '500px' }} />

            {/* ‚úÖ PlaceListComponentÏóê gyms(Ìó¨Ïä§Ïû• Î™©Î°ù) Ï†ÑÎã¨ */}
            {gyms.length > 0 && <PlaceListComponent gyms={gyms} />}
        </div>
    );
};

export default MapComponent;
