import React, {useEffect} from "react";

const MapComponent = () => {
    useEffect(() => {
        let map = null;

        const initMap = (lat, lng) => {
            const { naver } = window;
            if (!naver) return;

            console.log(naver.maps); // Services Í∞ùÏ≤¥Í∞Ä ÏûàÎäîÏßÄ ÌôïÏù∏
            console.log(naver.maps.Services); // undefinedÏù∏ÏßÄ ÌôïÏù∏
            console.log(naver.maps.Services.Places); // PlacesÍ∞Ä ÏûàÎäîÏßÄ ÌôïÏù∏

            map = new naver.maps.Map('map', {
                //  center: new naver.maps.LatLng(37.5665, 126.9780), //  Í∞ïÏ†ú Í≥†Ï†ï
                center: new naver.maps.LatLng(lat, lng), // ÏÇ¨Ïö©ÏûêÏùò ÌòÑÏû¨ ÏúÑÏπò
                zoom: 15,
            });

            //  ÎßàÏª§ Ï∂îÍ∞Ä
            new naver.maps.Marker({
                //  position: new naver.maps.LatLng(37.5665, 126.9780),
                position: new naver.maps.LatLng(lat, lng),
                map,
                title: "ÎÇ¥ ÏúÑÏπò"
            });

            // ÌòÑÏû¨ ÏúÑÏπò Í∏∞Î∞ò Í∑ºÏ≤ò Ìó¨Ïû• Í≤ÄÏÉâ
            const placesService = new naver.maps.Services.Places();   // ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ API ÏÑúÎπÑÏä§ Í∞ùÏ≤¥

            console.log(placesService);

            placesService.searchKeyword({
                query: "Ìó¨Ïä§Ïû•",
                coordinate: new naver.maps.LatLng(lat, lng),
                radius: 2000,
                page: 1,
                size: 10
            }, (status, response) => {
                if (status === naver.maps.Service.Status.ERROR) {
                    console.error("ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ API Í≤ÄÏÉâ Ïò§Î•ò Î∞úÏÉù");
                    return;
                }

                console.log("üîç Í≤ÄÏÉâÎêú Ìó¨Ïä§Ïû• Î™©Î°ù: ", response.places);

                response.places.forEach(place => {
                    const marker = new naver.maps.Marker({
                        position: new naver.maps.LatLng(place.y, place.x),
                        map,
                        title: place.name
                    });

                    const infoWindow = new naver.maps.InfoWindow({
                        content: `<div style="padding:10px;">${place.name}</div>`,
                    });

                    naver.maps.Event.addListener(marker, "click", () => {
                        infoWindow.open(map, marker);
                    });
                });
            });
        };

        const getCurrentPosition = () => {
            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(
                    (position) => {
                        const userLat = position.coords.latitude;
                        const userLng = position.coords.longitude;
                        //alert(`‚úÖ ÏúÑÏπò Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞ ÏÑ±Í≥µ: \nÏúÑÎèÑ: ${userLat} \nÍ≤ΩÎèÑ: ${userLng}`);
                        initMap(userLat, userLng); // Í∞ÄÏ†∏Ïò® ÏúÑÏπòÎ•º ÏßÄÎèÑ Ï¥àÍ∏∞Í∞íÏúºÎ°ú ÏÑ§Ï†ï
                    },
                    (error) => {
                        console.error("Geolocation Error: ", error);
                        //alert(`‚ùå Geolocation Error Î∞úÏÉù: \nÏΩîÎìú: ${error.code} \nÎ©îÏãúÏßÄ: ${error.message}`);
                        // Í∏∞Î≥∏ Ï¢åÌëú(ÏÑúÏö∏)Î°ú ÏßÄÎèÑ Î°úÎìú
                        initMap(37.5665, 126.9780);
                    },{
                        enableHighAccuracy: true,
                        timeout: 10000,
                        maximumAge: 0
                    }
                );
            } else {
                console.error("Geolocation is not supported by this browser.");
                initMap(37.5665, 126.9780); // Í∏∞Î≥∏ Ï¢åÌëú(ÏÑúÏö∏)Î°ú ÏßÄÎèÑ Î°úÎìú
            }
        };

        // ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ Ïä§ÌÅ¨Î¶ΩÌä∏ Î°úÎìú
        const script = document.createElement('script');
        script.src = `https://openapi.map.naver.com/openapi/v3/maps.js?ncpClientId=${process.env.REACT_APP_NAVER_MAP_CLIENT_ID}`;
        script.async = true;
        script.onload = getCurrentPosition;
        document.head.appendChild(script);
    }, []);

    return <div id="map" style={{ width: '100%', height: '400px' }} />;
}

export default MapComponent;
