import React, {useEffect} from "react";

const MapComponent = () => {
    useEffect(() => {
        let map = null;

        const initMap = (lat, lng) => {
            const { naver } = window;
            if (!naver) return;

            map = new naver.maps.Map('map', {
                //  center: new naver.maps.LatLng(37.5665, 126.9780), //  강제 고정
                center: new naver.maps.LatLng(lat, lng), // 사용자의 현재 위치
                zoom: 15,
            });

            //  마커 추가
            new naver.maps.Marker({
                //  position: new naver.maps.LatLng(37.5665, 126.9780),
                position: new naver.maps.LatLng(lat, lng),
                map,
                title: "내 위치"
            });
        };
        // 현재 위치 기반 근처 헬장 검색
        const placeService = naver.maps.Service; // 네이버 지도 API 서비스 객체

        placeService.searchKeyword({
           query:"헬스장",
           coordinate: new naver.maps.Lating(lat,lang),
            radius: 2000,
            page: 1,
            size: 10,
        },(staus,response) => {
            if(status === naver.maps.Services) {}
        });

        const getCurrentPosition = () => {
            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(
                    (position) => {
                        const userLat = position.coords.latitude;
                        const userLng = position.coords.longitude;
                        //alert(`✅ 위치 정보 가져오기 성공: \n위도: ${userLat} \n경도: ${userLng}`);
                        initMap(userLat, userLng); // 가져온 위치를 지도 초기값으로 설정
                    },
                    (error) => {
                        console.error("Geolocation Error: ", error);
                        //alert(`❌ Geolocation Error 발생: \n코드: ${error.code} \n메시지: ${error.message}`);
                        // 기본 좌표(서울)로 지도 로드
                        initMap(37.5665, 126.9780);
                    },{
                        enableHighAccuracy: true,
                        timeout: 10000,
                        maximumAge: 0
                    }
                );
            } else {
                console.error("Geolocation is not supported by this browser.");
                initMap(37.5665, 126.9780); // 기본 좌표(서울)로 지도 로드
            }
        };

        // 네이버 지도 스크립트 로드
        const script = document.createElement('script');
        script.src = `https://openapi.map.naver.com/openapi/v3/maps.js?ncpClientId=${process.env.REACT_APP_NAVER_MAP_CLIENT_ID}`;
        script.async = true;
        script.onload = getCurrentPosition;
        document.head.appendChild(script);
    }, []);

    return <div id="map" style={{ width: '100%', height: '400px' }} />;
}

export default MapComponent;
