import React, { useEffect, useState } from "react";
import PlaceListComponent from "./PlaceListComponent"; // Ìó¨Ïä§Ïû• Î™©Î°ù Ïª¥Ìè¨ÎÑåÌä∏

const MapComponent = () => {
    const [userLat, setUserLat] = useState(null);
    const [userLng, setUserLng] = useState(null);

    useEffect(() => {
        let map = null;

        const initMap = (lat, lng) => {
            const { naver } = window;
            if (!naver) return;

            map = new naver.maps.Map('map', {
                center: new naver.maps.LatLng(lat, lng),
                zoom: 15,
            });

            new naver.maps.Marker({
                position: new naver.maps.LatLng(lat, lng),
                map,
                title: "ÎÇ¥ ÏúÑÏπò"
            });

            // üìå ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏ (PlaceListComponentÏóê ÎÑòÍ≤®Ï§Ñ Í∞í Ï†ÄÏû•)
            setUserLat(lat);
            setUserLng(lng);
        };

        const getCurrentPosition = () => {
            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(
                    (position) => {
                        const lat = position.coords.latitude;
                        const lng = position.coords.longitude;
                        initMap(lat, lng);
                    },
                    (error) => {
                        console.error("Geolocation Error: ", error);
                        initMap(37.5665, 126.9780);
                    }, {
                        enableHighAccuracy: true,
                        timeout: 10000,
                        maximumAge: 0
                    }
                );
            } else {
                console.error("Geolocation is not supported by this browser.");
                initMap(37.5665, 126.9780);
            }
        };

        // ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ Ïä§ÌÅ¨Î¶ΩÌä∏ Î°úÎìú
        const script = document.createElement('script');
        script.src = `https://openapi.map.naver.com/openapi/v3/maps.js?ncpClientId=${process.env.REACT_APP_NAVER_MAP_CLIENT_ID}`;
        script.async = true;
        script.onload = getCurrentPosition;
        document.head.appendChild(script);
    }, []);

    return (
        <div>
            <h1>ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ ÌÖåÏä§Ìä∏</h1>
            <div id="map" style={{ width: '100%', height: '400px' }} />

            {/* üåç userLat & userLngÍ∞Ä ÏÑ§Ï†ïÎêòÎ©¥ PlaceListComponent Î†åÎçîÎßÅ */}
            {userLat && userLng && <PlaceListComponent lat={userLat} lng={userLng} />}
        </div>
    );
}

export default MapComponent;
