import React, { useEffect, useState } from "react";
import axios from "axios";

const MapComponent = () => {
    const [map, setMap] = useState(null);
    const [gyms, setGyms] = useState([]); // ‚úÖ Ìó¨Ïä§Ïû• Î™©Î°ù ÏÉÅÌÉú Ï∂îÍ∞Ä
    const [userLocation, setUserLocation] = useState(null); // ‚úÖ ÌòÑÏû¨ ÏúÑÏπò ÏÉÅÌÉú Ï∂îÍ∞Ä

    useEffect(() => {
        const initMap = (lat, lng) => {
            const { naver } = window;
            if (!naver) return;

            const mapInstance = new naver.maps.Map('map', {
                center: new naver.maps.LatLng(lat, lng),
                zoom: 15,
            });

            // ‚úÖ ÎÇ¥ ÏúÑÏπò ÎßàÏª§ Ï∂îÍ∞Ä
            new naver.maps.Marker({
                position: new naver.maps.LatLng(lat, lng),
                map: mapInstance,
                title: "ÎÇ¥ ÏúÑÏπò",
                icon: {
                    content: '<div style="color:red; font-weight:bold;">üìç</div>',
                    size: new naver.maps.Size(20, 20),
                    anchor: new naver.maps.Point(10, 10),
                }
            });

            setMap(mapInstance);
        };

        const getCurrentPosition = () => {
            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(
                    (position) => {
                        const userLat = position.coords.latitude;
                        const userLng = position.coords.longitude;
                        console.log("üìç ÌòÑÏû¨ ÏúÑÏπò:", userLat, userLng); // ‚úÖ ÎîîÎ≤ÑÍπÖÏö© Î°úÍ∑∏

                        setUserLocation({ lat: userLat, lng: userLng });
                        initMap(userLat, userLng);
                        fetchGyms(userLat, userLng); // ‚úÖ ÏúÑÏπò Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò® ÌõÑ Ìó¨Ïä§Ïû• Í≤ÄÏÉâ
                    },
                    (error) => {
                        console.error("‚ùå Geolocation Error:", error);
                        initMap(37.5665, 126.9780);
                    }, {
                        enableHighAccuracy: true,
                        timeout: 10000,
                        maximumAge: 0
                    }
                );
            } else {
                console.error("Geolocation is not supported by this browser.");
                initMap(37.5665, 126.9780);
            }
        };

        // ‚úÖ ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ Ïä§ÌÅ¨Î¶ΩÌä∏ Î°úÎìú
        const script = document.createElement('script');
        script.src = `https://openapi.map.naver.com/openapi/v3/maps.js?ncpClientId=${process.env.REACT_APP_NAVER_MAP_CLIENT_ID}`;
        script.async = true;
        script.onload = getCurrentPosition;
        document.head.appendChild(script);
    }, []);

    // ‚úÖ Ìó¨Ïä§Ïû• Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞
    const fetchGyms = async (lat, lng) => {
        try {
            console.log("üîç Ìó¨Ïä§Ïû• Í≤ÄÏÉâ ÏöîÏ≤≠:", lat, lng); // ‚úÖ ÏöîÏ≤≠ Ï†Ñ ÏúÑÏπò ÌôïÏù∏
            const response = await axios.get("http://localhost:8080/api/v1/places", {
                params: { lat, lng }
            });

            console.log("‚úÖ Ìó¨Ïä§Ïû• Í≤ÄÏÉâ Í≤∞Í≥º:", response.data.items);
            setGyms(response.data.items);
        } catch (error) {
            console.error("API Ìò∏Ï∂ú Ïò§Î•ò:", error);
        }
    };

    // ‚úÖ ÏßÄÎèÑ ÏúÑÏóê ÎßàÏª§ Ï∂îÍ∞Ä
    useEffect(() => {
        if (!map || gyms.length === 0) return;

        gyms.forEach((gym) => {
            new naver.maps.Marker({
                position: new naver.maps.LatLng(gym.mapy, gym.mapx), // ÎÑ§Ïù¥Î≤Ñ APIÎäî ÏúÑÎèÑ/Í≤ΩÎèÑ ÏàúÏÑúÍ∞Ä Î∞òÎåÄ
                map: map,
                title: gym.title,
                icon: {
                    content: `<div style="color:blue; font-weight:bold;">üèãÔ∏è‚Äç‚ôÇÔ∏è</div>`,
                    size: new naver.maps.Size(20, 20),
                    anchor: new naver.maps.Point(10, 10),
                }
            });
        });
    }, [map, gyms]); // ‚úÖ `gyms` Î≥ÄÍ≤ΩÎê† Îïå ÎßàÏª§ Ï∂îÍ∞Ä

    return (
        <div>
            <div id="map" style={{ width: '100%', height: '500px' }} />
            <h2>Í∑ºÏ≤ò Ìó¨Ïä§Ïû• Î™©Î°ù</h2>
            <ul>
                {gyms.map((gym, index) => (
                    <li key={index}>{gym.title}</li>
                ))}
            </ul>
        </div>
    );
};

export default MapComponent;
